source.buildSpec:
{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "npm install aws-cdk",
        "npm update",
        "pip install -r requirements.txt",
        "gem install cfn-nag"
      ]
    },
    "build": {
      "commands": [
        "npx cdk --version",
        "npx cdk synth",
        "cfn_nag_scan --input-path ec2Instance2.json",
        "cfn_nag_rules",
        "cfn_nag_scan --input-path cdk.out/ec2Instance2.template.json"
      ]
    }
  },
  "artifacts": {
    "files": [
      "**/*"
    ],
    "enable-symlinks": "yes"
  },
  "environment": {
    "buildImage": {
      "type": "LINUX_CONTAINER",
      "defaultComputeType": "BUILD_GENERAL1_SMALL",
      "imageId": "aws/codebuild/standard:2.0",
      "imagePullPrincipalType": "CODEBUILD"
    }
  }
}
